import React from 'react'
import { Link } from 'react-router-dom'
import { useState,useEffect } from 'react'
import { useSelector,useDispatch } from 'react-redux'
import { getQuizzD } from '../../actions/userActions'
import { useParams } from 'react-router-dom'

const Instructions= () =>{
    return (
        <>
        
        </>
    )
}

const PlayQuizz = () => {
  const {id}=useParams()
  const dispatch=useDispatch()
  useEffect(() => {
    dispatch(getQuizzD(id))
  }, [id])
  const [difficultyLevel,setDifficultyLevel] = useState(2)
  const [ans,setAns]=useState([])
  const { quizzDetails } = useSelector(
    (state) => state.appInfo
  );
  const next=async(event)=>{
    event.preventDefault()
    
  }
  function handleAns(e){
    setAns({...ans,[e.target.name]:e.target.checked})
  }
  return (
    <>
    {/* {true?<Instructions/>:fd} */}
    <div className="container my-4">
        <div className="question">
            <h4>Q. What is my name ?</h4>
            <b>Difficulty level : 5</b>
        </div>
        <div className="options">
        <div class="input-group mb-3 w-50 my-2">
  <div class="input-group-text">
    <input class="form-check" type="checkbox" value="" aria-label="Checkbox for following text input"/>
  </div>
  <input type="text" class="form-control" aria-label="Text input with checkbox"/>
</div>
<div class="input-group mb-3 w-50 my-2">
  <div class="input-group-text">
    <input class="form-check-input mt-0" type="checkbox" value="" aria-label="Checkbox for following text input" name="A" checked={ans.A} onChange={handleAns} id="A"/>
    <label htmlFor="A">Blockchain</label>
  </div>
  <input type="text" class="form-control" aria-label="Text input with checkbox"/>
</div>
<div class="input-group mb-3 w-50 my-2">
  <div class="input-group-text">
    <input class="form-check-input mt-0" type="checkbox" value="" aria-label="Checkbox for following text input"/>
  </div>
  <input type="text" class="form-control" aria-label="Text input with checkbox"/>
</div>
<div class="input-group mb-3 w-50 my-2">
  <div class="input-group-text">
    <input class="form-check-input mt-0" type="checkbox" value="" aria-label="Checkbox for following text input"/>
  </div>
  <input type="text" class="form-control" aria-label="Text input with checkbox"/>
</div>
        </div>
        <button class=" btn btn-sm btn-danger" type="submit" onClick={next} >Next</button>
    </div>
    </>
  )
}

export default PlayQuizz
