import React from 'react'
import { Link } from 'react-router-dom'
import { useState,useEffect } from 'react'
import { useSelector,useDispatch } from 'react-redux'
import { getQuizzD } from '../../actions/userActions'
import { useParams } from 'react-router-dom'

const Instructions= () =>{
    return (
        <>
        
        </>
    )
}

const PlayQuizz = () => {
  const {id}=useParams()
  const dispatch=useDispatch()
  useEffect(() => {
    dispatch(getQuizzD(id))
  }, [id])
  const [difficultyLevel,setDifficultyLevel] = useState(2)
  const [ans,setAns]=useState([])
  const { quizzDetails } = useSelector(
    (state) => state.appInfo
  );
  const next=async(event)=>{
    event.preventDefault()
    
  }
  function handleAns(e){
    setAns({...ans,[e.target.name]:e.target.checked})
  }
  console.log(ans,"d")
  const q=quizzDetails.quizz.questions[difficultyLevel-1]
  console.log(quizzDetails.quizz.questions[difficultyLevel-1])
  return (
    <>
    <div className="container my-4">
        <div className="question">
            <h4>Q. What is my name ?</h4>
            <b>Difficulty level : {difficultyLevel+1}</b>
        </div>
       

  <div class="form-check">
  <input class="form-check-input" type="checkbox" value="A"  id="A" name="A" checked={ans.A} onChange={handleAns}/>
  <label class="form-check-label" for="A">
    Default checkbox
  </label>
</div>
<div class="form-check my-2">
  <input class="form-check-input" type="checkbox" value="B" id="B" name="B" checked={ans.B} onChange={handleAns}/>
  <label class="form-check-label" for="B">
    Checked checkbox
  </label>
</div>
<div class="form-check my-2">
  <input class="form-check-input" type="checkbox" value="C" id="C" name="C" checked={ans.C} onChange={handleAns}/>
  <label class="form-check-label" for="C">
    Checked checkbox
  </label>
</div>
<div class="form-check my-2">
  <input class="form-check-input" type="checkbox" value="D" id="D" name="D" checked={ans.D} onChange={handleAns}/>
  <label class="form-check-label" for="D">
    Checked checkbox
  </label>
</div>

        <button class=" btn btn-sm btn-danger" type="submit" onClick={next} >Next</button>
    </div>
    </>
  )
}

export default PlayQuizz
